import "./App.css";
import React, { useState, useEffect } from "react";
import marked from "marked";

const useLocalStorage = (key, initialValue) => {
  const [value, setValue] = useState(() => {
    const storedValue = localStorage.getItem(key);
    return storedValue !== null ? JSON.parse(storedValue) : initialValue;
  });

  useEffect(() => {
    localStorage.setItem(key, JSON.stringify(value));
  }, [key, value]);

  return [value, setValue];
};

const App = () => {
  const [code, setCode] = useLocalStorage("markdown", "## Hello");
  const [compiled, setCompiled] = useState("");
  const [hide, hidePreview] = useState(true);
  const [docsContent, setDocsContent] = useState("");

  useEffect(() => {
    setCompiled(marked(code));
  }, [code]);

  useEffect(() => {
    fetchDocsContent();
  }, []);

  const openMD = () => {
    hidePreview(true);
  };

  const openPreview = () => {
    hidePreview(false);
  };

  const handleChange = (e) => {
    setCode(e.target.value);
  };

  const fetchDocsContent = async () => {
    try {
      const response = await fetch("https://www.markdownguide.org/api/v1");
      if (response.ok) {
        const data = await response.json();
        setDocsContent(data.content);
      } else {
        console.log("Failed to fetch docs content");
      }
    } catch (error) {
      console.log("Error occurred while fetching docs content:", error);
    }
  };

  return (
    <div className="app-container">
      <h1 className="title">Markdown Previewer React App</h1>
      <div className="container">
        <div className="btns">
          <button onClick={openMD} className={`btn ${hide ? "active" : ""}`}>
            MarkDown
          </button>
          <button
            onClick={openPreview}
            className={`btn ${!hide ? "active" : ""}`}
          >
            Preview
          </button>
          <button onClick={fetchDocsContent} className="btn">
            Docs
          </button>
        </div>
        <div className="editor-container">
          {hide ? (
            <textarea className="editor" onChange={handleChange} value={code} />
          ) : (
            <div
              className="preview"
              dangerouslySetInnerHTML={{ __html: compiled }}
            />
          )}
          {docsContent && (
            <div className="docs">
              <h2 className="docs-title">Docs</h2>
              <div dangerouslySetInnerHTML={{ __html: docsContent }} />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
