import "./App.css";
import React, { useState, useEffect } from "react";
import { marked } from "marked";
import syntaxData from "./syntax.json";

function useLocalStorage(key, initialValue) {
  const [value, setValue] = useState(() => {
    const storedValue = localStorage.getItem(key);
    return storedValue ? JSON.parse(storedValue) : initialValue;
  });

  useEffect(() => {
    localStorage.setItem(key, JSON.stringify(value));
  }, [key, value]);

  return [value, setValue];
}

const App = () => {
  const [code, setCode] = useLocalStorage("markdown", "## Hello");
  const [compiled, setCompiled] = useState('<h2 id="hello">Hello</h2>');
  const [activeTab, setActiveTab] = useState("Editor");
  // const [docsContent, setDocsContent] = useState("");

  const openMD = () => {
    setActiveTab("Editor");
  };

  const openPreview = () => {
    setActiveTab("Preview");
  };

  const openDocs = () => {
    setActiveTab("Docs");
  };

  const handleChange = (e) => {
    setCode(e.target.value);
    setCompiled(marked.parse(e.target.value));
  };

  // useEffect(() => {
  //   setDocsContent(JSON.stringify(syntaxData.basic_syntax));
  // }, []);

  const basicSyntax = syntaxData.basic_syntax;

  return (
    <>
      <h1>MarkDown Previewer React App</h1>
      <div className="container">
        <div className="btns">
          <button
            onClick={openMD}
            className={`btn ${activeTab === "Editor" ? "active" : ""}`}
          >
            MarkDown
          </button>
          <button
            onClick={openPreview}
            className={`btn ${activeTab === "Preview" ? "active" : ""}`}
          >
            Preview
          </button>
          <button
            onClick={openDocs}
            className={`btn ${activeTab === "Docs" ? "active" : ""}`}
          >
            Docs
          </button>
        </div>
        {activeTab === "Editor" && (
          <div>
            <textarea onChange={handleChange} value={code} />
          </div>
        )}
        {activeTab === "Preview" && (
          <div>
            <textarea readOnly value={compiled} />
          </div>
        )}
        {activeTab === "Docs" && (
          <div>
            <h2>Documentation</h2>
            <div>
              {basicSyntax.map((syntax, index) => (
                <div key={index}>
                  <h2>{syntax.name}</h2>
                  <p>{syntax.description}</p>
                  <h3>Examples:</h3>
                  {syntax.examples.map((example, exampleIndex) => (
                    <div key={exampleIndex}>
                      <pre style={{ background: "#f0f0f0", padding: "10px" }}>
                        <code>{example.markdown}</code>
                      </pre>
                      <div style={{ background: "#f0f0f0", padding: "10px" }}>
                        <code>{example.html}</code>
                      </div>
                    </div>
                  ))}
                  {/* Additional examples */}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </>
  );
};

export default App;
